mapper.select:
select

from t_course_item

	
		and tcs.course_schedule_id = courseScheduleId
	
	 		
		and tcs.course_date=#{courseDate}
	
	 			
		and tcs.school_id=#{schoolId}
	
	 			
		and tcs.classes_id=#{classesId}
	
	 		
		and INSTR(tci.teacher_name,#{teacherName})
	


	order by #{orderCol}
